function App() {
  const [canvas, setCanvas] = useState('');

  useEffect(() => {
    setCanvas(initCanvas());
    fabric.initFilterBackend();
  }, []);

  const initCanvas = () => (
    new fabric.Canvas('canvas', {
      height: 600,
      width: 600,
      backgroundColor: 'pink'
    })
  );

  const addImg = (canvi) => {
    fabric.Image.fromURL('testimg.jpeg', img => {
      img.filters.push(
        new fabric.Image.filters.Redify(),
        new fabric.Image.filters.Blur({ blur : 0.5 })
      );
      img.applyFilters();
      // canvi.setActiveObject(img);      
      canvi.add(img);
      canvi.renderAll();
    });
  }

  return (
    <div>
      <button onClick={() => addImg(canvas)}>Add Image</button>
      <canvas id="canvas" width="300" height="300" />
    </div>
  );
}

export default App;
