import './App.css';
import { useState, useEffect, useRef } from 'react';
import { fabric } from 'fabric';

fabric.Image.filters.Redify = fabric.util.createClass(fabric.Image.filters.BaseFilter, {
  type: 'Redify',

  /**
   * Fragment source for the redify program
   */
  fragmentSource: 'precision highp float;\n' +
    'uniform sampler2D uTexture;\n' +
    'varying vec2 vTexCoord;\n' +
    'void main() {\n' +
    'vec4 color = texture2D(uTexture, vTexCoord);\n' +
    'color.g = 0.0;\n' +
    'color.b = 0.0;\n' +
    'gl_FragColor = color;\n' +
    '}',

  applyTo2d: function (options) {
    var imageData = options.imageData,
      data = imageData.data, i, len = data.length;

    for (i = 0; i < len; i += 4) {
      data[i + 1] = 0;
      data[i + 2] = 0;
    }
  }
});

fabric.Image.filters.Redify.fromObject = fabric.Image.filters.BaseFilter.fromObject;

function App() {
  const [canvas, setCanvas] = useState('');
  const [imgURL, setImgURL] = useState('');

  useEffect(() => {
    setCanvas(initCanvas());
    fabric.initFilterBackend();
  }, []);

  const initCanvas = () => (
    new fabric.Canvas('canvas', {
      height: 800,
      width: 800,
      backgroundColor: 'pink'
    })
  );

  const addImg = (e, url, canvi) => {
    e.preventDefault();
    fabric.Image.fromURL('testimg.jpeg', img => {
      img.filters.push(
        new fabric.Image.filters.Redify(),
        new fabric.Image.filters.Blur({ blur : 0.5 })
      );
      img.applyFilters();
      // canvi.setActiveObject(img);      
      canvi.add(img);
      canvi.renderAll();
      setImgURL('');
    });
  }

  return (
    <div>
      <form onSubmit={e => addImg(e, imgURL, canvas)}>
        <div>
          <input
            type="text"
            value={imgURL}
            onChange={e => setImgURL(e.target.value)}
          />
          <button type="submit">Add Image</button>
        </div>
      </form>
      <canvas id="canvas" width="300" height="300" />
    </div>
  );
}

export default App;
